<?php/** * Manejo de las zonas activas de menú * * @package		Core * @category	GUI  * @access		public * @author		Alexis Lesa * @copyright	Advertis Web Factory * @licence 	Comercial * @version 	1.1 * @revision 	02.11.2010 * * @changelog: * 25.06.2012 *	- Modify: Se modificaron las funciones de la clase a públicas  *	por compatibilidad con métodos mágicos en PHP +5.4 * * 10.04.2012 *	- Modify: Se optimizó la clase. Se agregó información para documentación. * * @revision	 * @revision 	22.12.2011 * * @revision 	02.11.2010 * */class Menu {	/**	 * Array de datos para el menú	 *	 * @access private	 * @var array	 */	private $_arr;	/**	 * Constructor de la clase	 *	 * @access	public	 * @param	$pag_actual	string	Identificador de la página actual	 * @param	$home		boolean	Flag que indica si por defecto la clase no existe, carga como activo a "inicio"	 */	public function __construct($pag_actual=false, $home=false) {		if (!$pag_actual && $home) {			$pag_actual = 'inicio';		}				if ($pag_actual) {			$this->_arr[$pag_actual] = 'active';		}			}	/**	 * @access	private	 * @param	string	$clave	Nombre de la clave a consultar	 * @return	string	Texto "activo" si la clase esta definida, vacio o texto "inicio" en caso contrario	 */	public function __get($clave) {		return array_key_exists($clave, $this->_arr) ? $this->_arr[$clave] : '';	}	/**	 * Setea el valor del item de menú	 * 	 * @access	private	 * @param	string	$clave	Nombre del item	 * @param	string	$valor	Valor del item	 */	public function __set($clave, $valor) {		$this->_arr[$clave] = $valor;	}	/**	 * Inicialización de variables	 * 	 * @access	private	 */	public function init() {			$this->log = '<b>Información de actividad de la clase</b><br/>';		$this->log.= '- Clase:		'.$this->_name.'<br/>';		$this->log.= '- Versión:	'.$this->_version.'<br/>';		$this->log.= '- Fecha:		'.date("d.m.Y G:i:s").' <br/>';		$this->log.= '- Inicializando variables <br/>';		$this->_arr = array();		return true;	}}?>